    {
        double xPrev = baseR * (Math.Cos((t - 0.03)) + (t - 0.03) * Math.Sin((t - 0.03)));
        double yPrev = baseR * (Math.Sin((t - 0.03)) - (t - 0.03) * Math.Cos((t - 0.03)));
