double b = 0.020;    // face width 20mm
double mod = 0.002;  // module 2mm
double Y = 0.32;     // Lewis form factor (for ~24 teeth, 20-deg)
double sigma_b = Ft / (b * mod * Y);
Debug.WriteLine($"[OK] Bending stress = {sigma_b/1e6:F1} MPa (Lewis)");

