        if (val < nominalMin || val > nominalMax)
            Debug.WriteLine($"[WARN] {dim.FullName}: {val*1000:F2}mm (outside range)");
        dd = (DisplayDimension)f.GetNextDisplayDimension(dd);
    }
    f = (Feature)f.GetNextFeature();
}

